INSERT INTO `qs_company_profile` VALUES ('477', '526', ' ', '青岛艾海德电器有限公司', '50', '股份制企业', '17', '机械/设备/重工', '805', '0', '0', '青岛', '0', ' ', '83', '500-999人', '500', '万人民币', '山东青岛市黄岛区辛安福瀛装饰城际公寓', '蓝泽信息有限公司', '17263519072', '-', 'lanze@gmail.com', '', ' ', ' ', ' ', ' ', '1703/04/74071a673307ca7459bcf75fbd024e09.jpg', '青岛艾海德电器有限公司是一家生产微型泵及相关仪器的企业，主打产品有微型气泵、微型水泵、臭氧发生器等。我司有近十年的微型泵类产品设计、研发、生产、销售经验，拥有一支从事微型泵20余年经验的专业研发团队，以“缔造全球最大微型泵类产品供应商”为企业使命，始终坚持用实际行动来体现自身价值、做一个承担社会责任并让社会尊敬的企业；产品闻名全国乃至欧盟、美国、中东等国家和地区，在全球有两百余家客户；我们给客户提供微型泵及相关仪器的最佳解决方案，根据客户的特殊要求量身定做最佳的产品、OEM、贴牌、设计开发。', '1', '免费会员', '0', '0', '120.150920', '36.032629', '18', '1488272292', '1588617166', '1000', '1', '0', '0', '0', '0', '0', '0', '100', '145|五险,146|五险一金,147|长白班,149|年终奖,159|晋升空间', '0', '0', '0', '0', '0');
