INSERT INTO `qs_company_profile` VALUES ('718', '772', '', '青岛爱世给尔贸易有限公司', '54', '其它', '45', '其他行业', '805', '806', '0', '青岛/黄岛区', '0', '', '81', '20-99人', '', '万人民币', '青岛市黄岛区青岛爱世给尔贸易有限公司', '哒哒哒', '17090907865', '-', 'adminqweq@qq.com', '', '', '', '', '', '1703/05/50c3d7614917b24303ee6a220679dab3.jpg', '青岛爱世给尔贸易有限公司创建于1995年12月, 是由香港爱世给尔产业株式会社投资的香港独资企业，注册资金110万美元，坐落于秀丽富饶、寸土寸金的黄海之滨----青岛保税区，公司产品有:镶人造宝石K金、银手链、耳坠、戒指、项链、项链坠等，所用的原材料全部进口，产成品全部出口，产品远销日本、韩国、英国和美国等一些欧美国家。为青岛保税区十大出口企业之一。公司技术力量雄厚，设备先进。技术及管理人员都是由韩国聘请的专业人员。', '0', '', '0', '0', '120.183289', '35.982276', '18', '1488596842', '1588617194', '1', '1', '0', '0', '0', '0', '0', '0', '100', '163|压力小,145|环境好,149|加班费', '0', '0', '0', '0', '0');
INSERT INTO `qs_company_profile` VALUES ('719', '773', '', '青岛普什宝枫实业有限公司', '54', '其它', '45', '其他行业', '805', '806', '0', '青岛/黄岛区', '0', '', '80', '20人以下', '', '万人民币', '青岛市青岛保税区北京路40号', '电风扇', '18090908934', '-', 'admin432@qq.com', '', '', '', '', '', '1703/05/2afe4567e1bf64d32a5527244d104cea.png', '青岛普什宝枫实业有限公司是五粮液普什集团控股的以塑料制品生产、纸张加工为主导的中外合资企业，位于青岛保税区内，毗邻中国第三大集装箱码头-青岛前湾港，交通便利，享有保税区的各项优惠政策。P&amp;B于2005年投产，公司第一期生产以注塑产品为主，提供食品包装、薄壁容器及餐饮服务产品。2007年公司斥巨资兴建二期热成型产品生产厂房，10万级净化车间，从国外引进世界先进的正负压成型机、制杯机、多层片材挤出机、印杯机等一系列配套设备，吸收、转化国外先进技术，并在此基础上开发出反转模具、研发。。', '0', '', '0', '0', '120.194733', '35.989570', '18', '1488597236', '1588617194', '1', '1', '0', '0', '0', '0', '0', '0', '100', '154|工作轻松,156|全勤奖,159|晋升空间', '0', '0', '0', '0', '0');
